function (value) {
        var length = value.length
        var labelStr = ''
        if (length > 8) {
          var splitNum = Math.ceil(length / 8)
          if (splitNum === 2) {
            labelStr = value.substring(0, parseInt(length / 2)) + '\n' + value.substring(parseInt(length / 2))
          } else {
            for (var i = 0; i < splitNum; i++) {
              labelStr += value.substring(i * 8, (i + 1) * 8) + '\n'
            }
          }
        } else {
          labelStr = value
        }
        return labelStr
      }

